import { useState, useEffect } from "react";
import { AlertCircle, Map, Star, Zap, Flag, Target, Plus, X } from "lucide-react";

// Mock data for demonstration - would be replaced with actual API calls
const MOCK_SYSTEMS = [
    "Jita", "Amarr", "Dodixie", "Rens", "Hek", "1DQ1-A", "UALX-3", "Catch",
    "Providence", "Syndicate", "Venal", "Deklein", "Pure Blind", "Fountain"
];

// Mock regions for avoidance options
const MOCK_REGIONS = [
    "Delve", "Catch", "Esoteria", "Stain", "Providence", "Querious",
    "Period Basis", "Deklein", "Pure Blind", "Tribute", "Vale of the Silent"
];

// Sample response based on the JSON structure
const MOCK_API_RESPONSE = {
    "route": [
        { "system": "UALX-3", "action": "Start", "details": "Starting system", "region": "Catch" },
        { "system": "3L3N-X", "action": "Stargate", "details": "Standard jump", "region": "Catch" },
        { "system": "Q-S7ZD", "action": "Stargate", "details": "Standard jump", "region": "Catch" },
        { "system": "U-QVWD", "action": "Ansiblex", "details": "Jump Gate to 0SHT-A", "region": "Stain" },
        { "system": "0SHT-A", "action": "Titan", "details": "Bridge to Y-2ANO", "region": "Stain" },
        { "system": "Y-2ANO", "action": "Stargate", "details": "Standard jump", "region": "Esoteria" },
        { "system": "FWST-8", "action": "Stargate", "details": "Standard jump", "region": "Delve" },
        { "system": "1DQ1-A", "action": "Arrive", "details": "Destination reached", "region": "Delve" }
    ],
    "jumpRanges": {
        "isInSuperRange": false,
        "isInCapitalRange": true,
        "isInBlopsRange": true,
        "isInIndustrialRange": true
    }
};

export default function EVERoutePlanner() {
    const [sourceSystem, setSourceSystem] = useState("");
    const [destSystem, setDestSystem] = useState("");
    const [searchSource, setSearchSource] = useState("");
    const [searchDest, setSearchDest] = useState("");
    const [route, setRoute] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [filteredSourceSystems, setFilteredSourceSystems] = useState([]);
    const [filteredDestSystems, setFilteredDestSystems] = useState([]);

    // Added state for route options
    const [avoidTitanBridges, setAvoidTitanBridges] = useState(false);
    const [avoidAnsiblex, setAvoidAnsiblex] = useState(false);
    const [regionToAvoid, setRegionToAvoid] = useState("");
    const [avoidedRegions, setAvoidedRegions] = useState([]);
    const [filteredAvoidRegions, setFilteredAvoidRegions] = useState([]);
    const [systemToAvoid, setSystemToAvoid] = useState("");
    const [avoidedSystems, setAvoidedSystems] = useState([]);
    const [filteredAvoidSystems, setFilteredAvoidSystems] = useState([]);

    // Filter systems based on search input
    useEffect(() => {
        if (searchSource) {
            setFilteredSourceSystems(
                MOCK_SYSTEMS.filter(sys =>
                    sys.toLowerCase().includes(searchSource.toLowerCase())
                ).slice(0, 5)
            );
        } else {
            setFilteredSourceSystems([]);
        }
    }, [searchSource]);

    useEffect(() => {
        if (searchDest) {
            setFilteredDestSystems(
                MOCK_SYSTEMS.filter(sys =>
                    sys.toLowerCase().includes(searchDest.toLowerCase())
                ).slice(0, 5)
            );
        } else {
            setFilteredDestSystems([]);
        }
    }, [searchDest]);

    // Filter systems to avoid
    useEffect(() => {
        if (systemToAvoid) {
            setFilteredAvoidSystems(
                MOCK_SYSTEMS.filter(sys =>
                    sys.toLowerCase().includes(systemToAvoid.toLowerCase()) &&
                    !avoidedSystems.includes(sys)
                ).slice(0, 5)
            );
        } else {
            setFilteredAvoidSystems([]);
        }
    }, [systemToAvoid, avoidedSystems]);

    // Filter regions to avoid
    useEffect(() => {
        if (regionToAvoid) {
            setFilteredAvoidRegions(
                MOCK_REGIONS.filter(region =>
                    region.toLowerCase().includes(regionToAvoid.toLowerCase()) &&
                    !avoidedRegions.includes(region)
                ).slice(0, 5)
            );
        } else {
            setFilteredAvoidRegions([]);
        }
    }, [regionToAvoid, avoidedRegions]);

    // Add system to avoid list
    const addSystemToAvoid = (system) => {
        if (!avoidedSystems.includes(system)) {
            setAvoidedSystems([...avoidedSystems, system]);
            setSystemToAvoid("");
            setFilteredAvoidSystems([]);
        }
    };

    // Remove system from avoid list
    const removeSystem = (system) => {
        setAvoidedSystems(avoidedSystems.filter(s => s !== system));
    };

    // Add region to avoid list
    const addRegionToAvoid = (region) => {
        if (!avoidedRegions.includes(region)) {
            setAvoidedRegions([...avoidedRegions, region]);
            setRegionToAvoid("");
            setFilteredAvoidRegions([]);
        }
    };

    // Remove region from avoid list
    const removeRegion = (region) => {
        setAvoidedRegions(avoidedRegions.filter(r => r !== region));
    };

    // Handle route calculation
    const calculateRoute = () => {
        if (!sourceSystem || !destSystem) {
            setError("Please select both source and destination systems");
            return;
        }

        setLoading(true);
        setError(null);

        // Mock API call - would be replaced with actual fetch to the backend
        setTimeout(() => {
            // Simulate API response
            setRoute(MOCK_API_RESPONSE.route);
            setLoading(false);
        }, 1500);
    };

    // Render action icon based on the route step
    const renderActionIcon = (action) => {
        switch (action) {
            case "Stargate":
                return <Star className="text-blue-400" />;
            case "Titan":
                return <svg className="text-purple-400 h-5 w-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <circle cx="12" cy="12" r="10" />
                    <path d="M12 2v20M2 12h20M4.93 4.93l14.14 14.14M4.93 19.07l14.14-14.14" />
                </svg>;
            case "Ansiblex":
                return <svg className="text-yellow-400 h-5 w-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                    <path d="M5 12h14M12 5l7 7-7 7" />
                    <path d="M5 5l7 7-7 7" strokeDasharray="2" />
                </svg>;
            case "Start":
                return <Flag className="text-green-400" />;
            case "Arrive":
                return <Target className="text-green-400" />;
            default:
                return <Star className="text-gray-400" />;
        }
    };

    return (
        <div className="bg-gray-900 text-gray-200 min-h-screen p-6 bg-cover bg-fixed">
            <div className="max-w-4xl mx-auto backdrop-blur-sm bg-black bg-opacity-70 rounded-lg shadow-2xl border border-blue-900 p-6">
                <header className="mb-8 text-center">
                    <h1 className="text-4xl font-bold text-blue-400 mb-2 tracking-wider">CAP SAVE ROUTES (CSR)</h1>
                    <div className="h-1 w-32 bg-blue-500 mx-auto rounded-full mb-4"></div>
                    <p className="text-gray-400">Optimal jump routes for capital ship operations</p>
                </header>

                <div className="bg-black bg-opacity-70 rounded-lg p-6 mb-6 shadow-lg border border-gray-800">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Source System Selection */}
                        <div>
                            <label className="block text-blue-300 mb-2 uppercase text-sm tracking-wider">Source System</label>
                            <div className="relative">
                                <input
                                    type="text"
                                    value={searchSource}
                                    onChange={(e) => setSearchSource(e.target.value)}
                                    placeholder="Type to search systems..."
                                    className="w-full bg-black rounded px-4 py-2 border border-blue-900 focus:border-blue-500 focus:outline-none"
                                />
                                {filteredSourceSystems.length > 0 && (
                                    <div className="absolute z-10 mt-1 w-full bg-black border border-blue-900 rounded shadow-lg">
                                        {filteredSourceSystems.map((sys) => (
                                            <div
                                                key={sys}
                                                className="px-4 py-2 cursor-pointer hover:bg-blue-900 hover:bg-opacity-50"
                                                onClick={() => {
                                                    setSourceSystem(sys);
                                                    setSearchSource(sys);
                                                    setFilteredSourceSystems([]);
                                                }}
                                            >
                                                {sys}
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                            {sourceSystem && (
                                <div className="mt-2 text-green-400">
                                    Selected: {sourceSystem}
                                </div>
                            )}
                        </div>

                        {/* Destination System Selection */}
                        <div>
                            <label className="block text-blue-300 mb-2 uppercase text-sm tracking-wider">Destination System</label>
                            <div className="relative">
                                <input
                                    type="text"
                                    value={searchDest}
                                    onChange={(e) => setSearchDest(e.target.value)}
                                    placeholder="Type to search systems..."
                                    className="w-full bg-black rounded px-4 py-2 border border-blue-900 focus:border-blue-500 focus:outline-none"
                                />
                                {filteredDestSystems.length > 0 && (
                                    <div className="absolute z-10 mt-1 w-full bg-black border border-blue-900 rounded shadow-lg">
                                        {filteredDestSystems.map((sys) => (
                                            <div
                                                key={sys}
                                                className="px-4 py-2 cursor-pointer hover:bg-blue-900 hover:bg-opacity-50"
                                                onClick={() => {
                                                    setDestSystem(sys);
                                                    setSearchDest(sys);
                                                    setFilteredDestSystems([]);
                                                }}
                                            >
                                                {sys}
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                            {destSystem && (
                                <div className="mt-2 text-green-400">
                                    Selected: {destSystem}
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Jump Range Status (for FC decision making) */}
                    {sourceSystem && destSystem && (
                        <div className="mt-6 bg-blue-900 bg-opacity-20 rounded-lg p-4 border border-blue-800">
                            <h3 className="text-lg font-medium text-blue-400 mb-3">Jump Capability Status</h3>
                            <div className="grid grid-cols-2 gap-3 md:grid-cols-4">
                                <div className={`p-3 rounded border ${MOCK_API_RESPONSE.jumpRanges.isInSuperRange ? 'bg-green-900 bg-opacity-30 border-green-700 text-green-400' : 'bg-red-900 bg-opacity-30 border-red-700 text-red-400'}`}>
                                    <div className="font-medium">Supercarrier</div>
                                    <div className="text-sm">{MOCK_API_RESPONSE.jumpRanges.isInSuperRange ? 'In Range' : 'Out of Range'}</div>
                                </div>
                                <div className={`p-3 rounded border ${MOCK_API_RESPONSE.jumpRanges.isInCapitalRange ? 'bg-green-900 bg-opacity-30 border-green-700 text-green-400' : 'bg-red-900 bg-opacity-30 border-red-700 text-red-400'}`}>
                                    <div className="font-medium">Capital Ships</div>
                                    <div className="text-sm">{MOCK_API_RESPONSE.jumpRanges.isInCapitalRange ? 'In Range' : 'Out of Range'}</div>
                                </div>
                                <div className={`p-3 rounded border ${MOCK_API_RESPONSE.jumpRanges.isInBlopsRange ? 'bg-green-900 bg-opacity-30 border-green-700 text-green-400' : 'bg-red-900 bg-opacity-30 border-red-700 text-red-400'}`}>
                                    <div className="font-medium">Black Ops</div>
                                    <div className="text-sm">{MOCK_API_RESPONSE.jumpRanges.isInBlopsRange ? 'In Range' : 'Out of Range'}</div>
                                </div>
                                <div className={`p-3 rounded border ${MOCK_API_RESPONSE.jumpRanges.isInIndustrialRange ? 'bg-green-900 bg-opacity-30 border-green-700 text-green-400' : 'bg-red-900 bg-opacity-30 border-red-700 text-red-400'}`}>
                                    <div className="font-medium">Jump Freighter</div>
                                    <div className="text-sm">{MOCK_API_RESPONSE.jumpRanges.isInIndustrialRange ? 'In Range' : 'Out of Range'}</div>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Route Options */}
                    <div className="mt-6 bg-blue-900 bg-opacity-10 rounded-lg p-4 border border-blue-900">
                        <h3 className="text-lg font-medium text-blue-400 mb-3">Route Options</h3>

                        {/* Jump Type Options */}
                        <div className="mb-4 grid grid-cols-1 md:grid-cols-2 gap-3">
                            <div className="flex items-center">
                                <input
                                    type="checkbox"
                                    id="avoidTitan"
                                    checked={avoidTitanBridges}
                                    onChange={() => setAvoidTitanBridges(!avoidTitanBridges)}
                                    className="form-checkbox rounded bg-gray-900 border-blue-500 text-blue-500 focus:ring-blue-500"
                                />
                                <label htmlFor="avoidTitan" className="ml-2 text-blue-200">Avoid Titan Bridges</label>
                            </div>
                            <div className="flex items-center">
                                <input
                                    type="checkbox"
                                    id="avoidAnsiblex"
                                    checked={avoidAnsiblex}
                                    onChange={() => setAvoidAnsiblex(!avoidAnsiblex)}
                                    className="form-checkbox rounded bg-gray-900 border-blue-500 text-blue-500 focus:ring-blue-500"
                                />
                                <label htmlFor="avoidAnsiblex" className="ml-2 text-blue-200">Avoid Ansiblex Jump Gates</label>
                            </div>
                        </div>

                        {/* Avoid Regions */}
                        <div className="mb-6">
                            <label className="block text-blue-300 mb-2 uppercase text-sm tracking-wider">Avoid Regions</label>
                            <div className="flex items-center">
                                <div className="relative flex-1">
                                    <input
                                        type="text"
                                        value={regionToAvoid}
                                        onChange={(e) => setRegionToAvoid(e.target.value)}
                                        placeholder="Search for regions to avoid..."
                                        className="w-full bg-black rounded px-4 py-2 border border-blue-900 focus:border-blue-500 focus:outline-none"
                                    />
                                    {filteredAvoidRegions.length > 0 && (
                                        <div className="absolute z-10 mt-1 w-full bg-black border border-blue-900 rounded shadow-lg">
                                            {filteredAvoidRegions.map((region) => (
                                                <div
                                                    key={region}
                                                    className="px-4 py-2 cursor-pointer hover:bg-blue-900 hover:bg-opacity-50"
                                                    onClick={() => addRegionToAvoid(region)}
                                                >
                                                    {region}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                                <button
                                    onClick={() => {
                                        if (regionToAvoid && !avoidedRegions.includes(regionToAvoid)) {
                                            addRegionToAvoid(regionToAvoid);
                                        }
                                    }}
                                    className="ml-2 bg-blue-900 hover:bg-blue-800 p-2 rounded"
                                >
                                    <Plus size={20} />
                                </button>
                            </div>

                            {avoidedRegions.length > 0 && (
                                <div className="mt-3 flex flex-wrap gap-2">
                                    {avoidedRegions.map(region => (
                                        <div key={region} className="bg-blue-900 bg-opacity-50 text-blue-200 rounded-full px-3 py-1 text-sm flex items-center">
                                            {region}
                                            <button
                                                onClick={() => removeRegion(region)}
                                                className="ml-2 text-blue-300 hover:text-blue-100"
                                            >
                                                <X size={14} />
                                            </button>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>

                        {/* Avoid Systems */}
                        <div className="mb-6">
                            <label className="block text-blue-300 mb-2 uppercase text-sm tracking-wider">Avoid Specific Systems</label>
                            <div className="flex items-center">
                                <div className="relative flex-1">
                                    <input
                                        type="text"
                                        value={systemToAvoid}
                                        onChange={(e) => setSystemToAvoid(e.target.value)}
                                        placeholder="Search for systems to avoid..."
                                        className="w-full bg-black rounded px-4 py-2 border border-blue-900 focus:border-blue-500 focus:outline-none"
                                    />
                                    {filteredAvoidSystems.length > 0 && (
                                        <div className="absolute z-10 mt-1 w-full bg-black border border-blue-900 rounded shadow-lg">
                                            {filteredAvoidSystems.map((sys) => (
                                                <div
                                                    key={sys}
                                                    className="px-4 py-2 cursor-pointer hover:bg-blue-900 hover:bg-opacity-50"
                                                    onClick={() => addSystemToAvoid(sys)}
                                                >
                                                    {sys}
                                                </div>
                                            ))}
                                        </div>
                                    )}
                                </div>
                                <button
                                    onClick={() => {
                                        if (systemToAvoid && !avoidedSystems.includes(systemToAvoid)) {
                                            addSystemToAvoid(systemToAvoid);
                                        }
                                    }}
                                    className="ml-2 bg-blue-900 hover:bg-blue-800 p-2 rounded"
                                >
                                    <Plus size={20} />
                                </button>
                            </div>

                            {avoidedSystems.length > 0 && (
                                <div className="mt-3 flex flex-wrap gap-2">
                                    {avoidedSystems.map(system => (
                                        <div key={system} className="bg-blue-900 bg-opacity-50 text-blue-200 rounded-full px-3 py-1 text-sm flex items-center">
                                            {system}
                                            <button
                                                onClick={() => removeSystem(system)}
                                                className="ml-2 text-blue-300 hover:text-blue-100"
                                            >
                                                <X size={14} />
                                            </button>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>

                    <div className="mt-6">
                        <button
                            onClick={calculateRoute}
                            disabled={loading}
                            className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-6 rounded transition duration-200 disabled:opacity-50 uppercase tracking-wider"
                        >
                            {loading ? "Calculating..." : "Find Route"}
                        </button>
                    </div>

                    {error && (
                        <div className="mt-4 bg-red-900 bg-opacity-70 border border-red-700 text-red-200 px-4 py-3 rounded flex items-center">
                            <AlertCircle className="mr-2" />
                            {error}
                        </div>
                    )}
                </div>

                {/* Route Display */}
                {route && (
                    <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
                        <h2 className="text-xl font-bold text-blue-400 mb-4">Route: {sourceSystem} → {destSystem}</h2>

                        <div className="space-y-2">
                            {route.map((step, index) => (
                                <div
                                    key={index}
                                    className="flex items-center p-3 border border-gray-700 rounded bg-opacity-50 bg-gray-800 hover:bg-gray-700"
                                >
                                    <div className="mr-3">
                                        {renderActionIcon(step.action)}
                                    </div>
                                    <div className="flex-grow">
                                        <div className="font-medium">{step.system}</div>
                                        <div className="text-gray-400 text-sm">{step.details}</div>
                                        <div className="text-gray-500 text-xs">{step.region}</div>
                                    </div>
                                </div>
                            ))}
                        </div>

                        <div className="mt-6 bg-gray-700 rounded p-4 border border-gray-700">
                            <div className="flex flex-col md:flex-row md:justify-between text-gray-300">
                                <div>Total Jumps: <span className="text-white font-medium">{route.length - 2}</span></div>
                                <div>Estimated Time: <span className="text-white font-medium">~{Math.round((route.length - 2) * 2.5)} minutes</span></div>
                            </div>
                        </div>

                        {/* Route Options Applied */}
                        <div className="mt-6 bg-gray-700 rounded p-4 border border-gray-600">
                            <h3 className="text-sm font-medium text-gray-300 mb-2">Route Calculation Settings:</h3>
                            <div className="text-xs text-gray-400">
                                <div>{avoidTitanBridges ? "✓" : "✗"} Avoiding Titan Bridges</div>
                                <div>{avoidAnsiblex ? "✓" : "✗"} Avoiding Ansiblex Jump Gates</div>
                                <div>
                                    {avoidedRegions.length > 0
                                        ? `✓ Avoiding Regions: ${avoidedRegions.join(", ")}`
                                        : "✗ No regions avoided"}
                                </div>
                                <div>
                                    {avoidedSystems.length > 0
                                        ? `✓ Avoiding Systems: ${avoidedSystems.join(", ")}`
                                        : "✗ No systems avoided"}
                                </div>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
